import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Image, FlatList, TouchableWithoutFeedback, Switch,ActivityIndicator } from 'react-native';
import {  useSelector } from 'react-redux';
import { ref, onValue, push, update, remove } from 'firebase/database';
import { db } from '../../firebase-config';
import { baseStyles } from '../Styles/styles';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { ImageBackground } from 'react-native';
import { useNavigation } from '@react-navigation/core'
import backgrounds from '../../Enumeration/backgrounds';

const DATA = [
  {
    id: 1,
    background: require('../../assets/livingrom2.png'),
    title: 'Living Room',
    infoEquipment: [
      {
        idEquipment: 1,
        iconEquipment: 'air-conditioner',
        nameEquipment: 'Air Conditioner',
        state: true,
        data: '25C'
      },
      {
        idEquipment: 2,
        iconEquipment: 'air-conditioner',
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        idEquipment: 3,
        iconEquipment: 'air-conditioner',
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        idEquipment: 4,
        iconEquipment: 'air-conditioner',
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      }
    ]
  },
  {
    id: 2,
    background: require('../../assets/mediaroom2.png'),
    title: 'Media Room',
    infoEquipment: [
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      }
    ]
  },
  {
    id: 3,
    background: require('../../assets/bathroom2.png'),
    title: 'Bath Room',
    infoEquipment: [
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: true,
        data: '25C'
      }
    ]
  },
  {
    id: 4,
    background: require('../../assets/bedroom2.png'),
    title: 'Bed Room',
    infoEquipment: [
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      },
      {
        iconEquipment: "air-conditioner",
        nameEquipment: 'Air Conditioner',
        state: false,
        data: '25C'
      }
    ]
  }
];

const RoomScreen = (props) => {
  const [isLoading, setIsLoading] = useState(false);
  const homeId = useSelector((store) => store.state.homeId);
  const [roomActive, setRoomActive] = useState(1);
  const [room, setRoom] = useState(DATA[0]);
  const navigation = useNavigation();

  useEffect(() => {
    

    return onValue(ref(db, '/Homes/' + homeId), querySnapShot => {
      let data = querySnapShot.val() || [];
      //alert(JSON.stringify(data))
      // nếu đi từ home sang thì xử lý theo data home gửi sang
      if (props.route && props.route.params && props.route.params.options && props.route.params.options.data) {
        // setRoom(props.route.params.options.data);
        // setRoomActive(props.route.params.options.data.id)
      }
      else{

      }
      setIsLoading(true);
    });
  }, [props]);

  const changeRoom = (item) => {
    setRoom(item);
    setRoomActive(item.id)
  }

  const onOpenViewProfile = () => {
    navigation.replace("Profile", {
      options: {
        parent: "Rooms"
      }
    })
  }

  const renderItem = ({ item, index, separators }) => (
    <TouchableWithoutFeedback onPress={() => changeRoom(item)}>
      <ImageBackground source={backgrounds[index]} resizeMode='cover' imageStyle={{ borderRadius: 20, width: '100%', height: '100%' }}
        style={[styles.item, roomActive == item.id ? styles.activeRoom : ""]}>
        <View style={styles.roomName}>
          <Text style={styles.title}>{item.title}</Text>
        </View>
      </ImageBackground>
    </TouchableWithoutFeedback>
  );

  return isLoading ?
    (
      <View style={styles.container}>
        <View style={baseStyles.row}>
          <View>
            <Text style={styles.dateTime}>February 13, 2020</Text>
            <Text style={baseStyles.h1}>Rooms</Text>
          </View>
          <TouchableWithoutFeedback onPress={() => onOpenViewProfile()}>
            <View style={styles.avatar}>
              <Image
                style={styles.tinyLogo}
                source={require('../../assets/avatar.png')}
              />
            </View>
          </TouchableWithoutFeedback>

        </View>
        <View style={styles.rooms}>
          <View >
            <FlatList
              data={DATA}
              horizontal
              showsHorizontalScrollIndicator={false}
              pagingEnabled
              contentOffset={{ x: (room.id - 1) * 100, y: 0 }}
              renderItem={renderItem}
              keyExtractor={item => item.id}
            />
          </View>
          <View style={styles.dataRoom}>
            <Text style={styles.titleRoom}>{room.title}</Text>
            <View style={styles.equipments}>
              {
                room.infoEquipment.map((element, index) => {
                  return (
                    <TouchableWithoutFeedback key={index}>
                      <View style={[styles.equipment, element.state ? styles.backgroundActive : ""]}>
                        <View style={{ marginLeft: 'auto', marginTop: 10 }}>
                          <Switch
                            style={{ transform: [{ scaleX: 0.7 }, { scaleY: 0.7 }] }}
                            trackColor={{ false: "#767577", true: "#FA6400" }}
                            thumbColor={"#FFFFFF"}
                            ios_backgroundColor="#707070"
                            value={element.state} />
                        </View>
                        <View style={{ marginLeft: 25 }}>
                          <MaterialCommunityIcons name="lightbulb-on-outline" size={40} color={element.state ? "#FA6400" : "black"} />
                        </View>
                        <View style={styles.nameEquipment}>
                          <Text style={{ fontSize: 17, fontWeight: '400' }}>{element.nameEquipment}</Text>
                          <Text style={baseStyles.p}>{element.data}</Text>
                        </View>
                      </View>
                    </TouchableWithoutFeedback>
                  );
                })
              }
            </View>
          </View>
        </View>
      </View>
    ) :
    (<View style={baseStyles.loaddingContainer}>
      <ActivityIndicator size="large" color="#FA6400" />
    </View>)
    ;
}

// style
const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 80,
    width: '100%',
    height: '100%',
    backgroundColor: '#fff'
  },
  avatar: {
    width: 50,
    height: 50,
    marginLeft: 'auto',
    borderRadius: '50%',
    marginTop: 5

  },
  tinyLogo: {
    width: '100%',
    height: '100%',
    borderRadius: '50%',
  },
  rooms: {
    marginTop: 10,
    width: '90%',
    marginLeft: '5%'
  },
  item: {
    backgroundColor: '#fff',
    width: 150,
    height: 150,
    marginBottom: 5,
    position: 'relative',
    marginHorizontal: 8,
    borderRadius: 20,
  },
  activeRoom: {
    width: 160,
    height: 160,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.32,
    shadowRadius: 5.46,

    elevation: 9,
  },
  title: {
    fontSize: 15,
    fontWeight: '500',
    color: 'white',
  },
  dateTime: {
    fontSize: 15,
    color: '#9BA4B0'
  },
  roomName: {
    marginLeft: '10%',
    position: 'absolute',
    bottom: 15
  },
  dataRoom: {
    marginTop: 20,
    width: '100%',
  },
  titleRoom: {
    fontSize: 23,
    fontWeight: '500'
  },
  equipments: {
    flexDirection: 'row',
    flexWrap: 'wrap'
  },
  equipment: {
    width: '45%',
    height: 180,
    marginTop: 20,
    marginRight: '2%',
    marginLeft: '2%',
    borderWidth: .5,
    borderRadius: 20,
    borderColor: '#B4BBC4',
    position: 'relative'
  },
  nameEquipment: {
    position: 'absolute',
    bottom: 20,
    left: 25,
  },
  backgroundActive: {
    backgroundColor: '#edebeb',
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.27,
    shadowRadius: 4.65,

    elevation: 6,
  }
});


export default RoomScreen;
