import React, { useEffect, useState } from 'react'
import { useNavigation } from '@react-navigation/core'
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import HomeScreen from './HomeScreen';
import RoomScreen from './RoomScreen';
import SettingScreen from './SettingScreen';
import { Ionicons } from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    onAuthStateChanged,
    FacebookAuthProvider,
    signInWithCredential,
} from 'firebase/auth';
import { auth } from '../../firebase-config'
import { db } from '../../firebase-config'
import { useDispatch, useSelector } from 'react-redux';
import { onValue, ref } from 'firebase/database';
import { setDataUser, setDataHome } from '../Actions/counts';

const Tab = createBottomTabNavigator();

const MainScreen = (props) => {

    const dispatch = useDispatch();

    // 
    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(user => {
            if (!user) {
                navigation.replace("Login")
            }
            else {
                if (user.uid) {
                    const user = onValue(ref(db, '/users/' + user.uid), querySnapShot => {
                        let data = querySnapShot.val() || [];
                        dispatch(setDataUser(data));

                        const home = onValue(ref(db, '/Homes/' + data.home), querySnapShot => {
                            let dataHome = querySnapShot.val() || [];
                            dispatch(setDataHome(dataHome));
                        });
                    });
                }
            }
        })

        return unsubscribe
    }, [])

    const getTabActive = () => {
        let tabActive = "Home";
        if (props.route && props.route.params && props.route.params.options && props.route.params.options.tabActive) {
            tabActive = props.route.params.options.tabActive;
        }
        return tabActive;
    }


    return (
        <Tab.Navigator initialRouteName={getTabActive()}>
            <Tab.Screen options={{
                headerShown: false,
                tabBarActiveTintColor: "#FA6400",
                tabBarIcon: ({ color, size }) => (
                    <Ionicons name="home" color={color} size={size} />
                )
            }} name="Home" component={HomeScreen} />
            <Tab.Screen options={{
                headerShown: false,
                tabBarActiveTintColor: "#FA6400",
                tabBarIcon: ({ color, size }) => (
                    <Entypo name="sweden" color={color} size={size} />
                )
            }} name="Rooms" component={RoomScreen} />
            <Tab.Screen options={{
                headerShown: false,
                tabBarActiveTintColor: "#FA6400",
                tabBarIcon: ({ color, size }) => (
                    <SimpleLineIcons name="settings" color={color} size={size} />
                )
            }} name="Settings" component={SettingScreen} />
        </Tab.Navigator>
    );
}

export default MainScreen;
